INSERT INTO `wp_posts` VALUES ('6', '1', '2013-11-16 05:52:06', '2013-11-16 05:52:06', '<div id=\"wrapper\">\r\n<div id=\"head\">\r\n<h1><a title=\"Edit in JSFiddle\" href=\"http://jsfiddle.net/drewgreenwell/Xqh8u/light/\" target=\"new\">Edit in JSFiddle</a></h1>\r\n<div id=\"actions\">\r\n<ul>\r\n	<li><a title=\"Result\" href=\"http://jsfiddle.net/drewgreenwell/Xqh8u/embedded/result,html,js,resources/?version=0.9.72#Result\" data-trigger-type=\"result\">Result</a></li>\r\n	<li><a title=\"HTML\" href=\"http://jsfiddle.net/drewgreenwell/Xqh8u/embedded/result,html,js,resources/?version=0.9.72#HTML\" data-trigger-type=\"html\">HTML</a></li>\r\n	<li><a title=\"JavaScript\" href=\"http://jsfiddle.net/drewgreenwell/Xqh8u/embedded/result,html,js,resources/?version=0.9.72#JavaScript\" data-trigger-type=\"js\">JavaScript</a></li>\r\n	<li><a title=\"Resources\" href=\"http://jsfiddle.net/drewgreenwell/Xqh8u/embedded/result,html,js,resources/?version=0.9.72#Resources\" data-trigger-type=\"resources\">Resources</a></li>\r\n</ul>\r\n</div>\r\n</div>\r\n<div id=\"tabs\">\r\n<pre data-language=\"html\">&lt;h1&gt;Slide Mode Simple&lt;/h1&gt;\r\n&lt;div class=\"red\"&gt;\r\n    &lt;div id=\"tile1\" class=\"live-tile\"&gt;\r\n        &lt;span class=\"tile-title\"&gt;tile title&lt;/span&gt;\r\n        &lt;!-- adding the \'full\' class to an \'img\' or \'a\' tag causes it to fill the entire tile --&gt;\r\n        &lt;div&gt;&lt;a class=\"full\" href=\"#\"&gt;front&lt;/a&gt;&lt;/div&gt;\r\n        &lt;div&gt;&lt;p&gt;the front face slides vertically by default to reveal the back.&lt;/p&gt;&lt;/div&gt;\r\n    &lt;/div&gt;\r\n    &lt;div id=\"tile2\" class=\"live-tile blue\" data-direction=\"horizontal\"&gt;       \r\n   &lt;span class=\"tile-title\"&gt;tile title&lt;/span&gt;\r\n        &lt;div&gt;front&lt;/div&gt;\r\n        &lt;div&gt;&lt;p&gt;the front tile slides horizontally to reveal the back because data-direction is set to \'horizontal\'&lt;/p&gt;&lt;/div&gt;\r\n    &lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;div class=\"clear\"&gt;\r\n    &lt;h2&gt;Notes&lt;/h2&gt;\r\n    &lt;p&gt;Adding the \'full\' class to an \'img\' or \'a\' tag causes it to fill the entire tile&lt;br /&gt;\r\n            You can use the data-stack property to move the the tiles in unison (like the \r\n            &lt;a href=\"http://www.drewgreenwell.com\" target=\"_blank\"&gt;home page&lt;/a&gt;)&lt;/div&gt;</pre>\r\n</div>\r\n</div>', 'Home', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2013-11-16 05:52:06', '2013-11-16 05:52:06', '', '4', 'http://aps_parent_portal.localhost/?p=6', '0', 'revision', '', '0');
